                "color": "Country",
                "label": "Value 2 (+)",
            },
            "coordSystem": "polar",
        }
    )
)

chart.animate(
    Config(
        {
            "channels": {
                "x": ["Year", "Value 2 (+)"],
                "y": {"set": "Country", "range": {"min": "-30%"}},
                "label": None,
            }
        }
    )
)

chart.animate(
    Config(
        {
            "channels": {
                "x": "Year",
                "y": {
                    "set": ["Country", "Value 2 (+)"],
                    "range": {"min": "-30%"},
                },
            }
        }
    ),
    Style({"plot": {"marker": {"rectangleSpacing": "0.1em"}}}),
)

chart.feature("tooltip", True)</code></pre>

